import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const FormSection = ({ title, fields, endpoint, isActive, onClose }) => {
  const [formData, setFormData] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      
      if (response.ok) {
        alert(`${title} submitted successfully!`);
        setFormData({});
        onClose();
      } else {
        try {
          const errorData = await response.json();
          alert(
            `Failed to submit ${title}: ${errorData.error || errorData.message || "Unknown error"}`
          );
        } catch (parseError) {
          alert(`Failed to submit ${title}: ${response.statusText}`);
        }
      }
    } catch (error) {
      console.error(`Error submitting ${title}:`, error);
      alert(`An error occurred while submitting ${title}.`);
    }
  };

  return (
    <div 
      style={{
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: isActive ? 'translate(-50%, -50%) scale(1)' : 'translate(-50%, -50%) scale(0.8)',
        opacity: isActive ? 1 : 0,
        width: '90%',
        maxWidth: '500px',
        backgroundColor: 'rgba(25, 25, 45, 0.95)',
        borderRadius: '20px',
        padding: '25px',
        boxShadow: '0 0 30px rgba(0, 200, 255, 0.5)',
        zIndex: 100,
        transition: 'all 0.4s ease-in-out',
        pointerEvents: isActive ? 'all' : 'none',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(0, 200, 255, 0.3)',
        maxHeight: '90vh',
        overflowY: 'auto'
      }}
      onClick={(e) => e.stopPropagation()} // Prevent clicks inside form from closing it
    >
      {/* ... rest of your FormSection code remains the same ... */}
    </div>
  );
};

