import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const FormSection = ({ title, fields, endpoint }) => {
  const [formData, setFormData] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      
      if (response.ok) {
        alert(`${title} submitted successfully!`);
        setFormData({});
      } else {
        try {
          const errorData = await response.json();
          alert(
            `Failed to submit ${title}: ${errorData.error || errorData.message || "Unknown error"}`
          );
        } catch (parseError) {
          alert(`Failed to submit ${title}: ${response.statusText}`);
        }
      }
    } catch (error) {
      console.error(`Error submitting ${title}:`, error);
      alert(`An error occurred while submitting ${title}.`);
    }
  };

  return (
    <div style={{
      backgroundColor: 'rgba(25, 25, 45, 0.8)',
      borderRadius: '20px',
      padding: '25px',
      marginBottom: '30px',
      boxShadow: '0 0 30px rgba(0, 200, 255, 0.3)',
      border: '1px solid rgba(0, 200, 255, 0.3)',
      transition: 'all 0.3s ease',
    }}>
      <h2 style={{ 
        color: '#00ccff', 
        marginTop: 0,
        marginBottom: '20px',
        textShadow: '0 0 10px rgba(0, 200, 255, 0.7)'
      }}>
        {title}
      </h2>
      
      <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '15px' }}>
        {fields.map((field) => (
          <div key={field.key} style={{ position: 'relative' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              color: '#e0e0ff',
              fontWeight: '500'
            }}>
              {field.label}:
            </label>
            <input
              type="text"
              value={formData[field.key] || ""}
              onChange={(e) => setFormData({ ...formData, [field.key]: e.target.value })}
              style={{
                width: '100%',
                padding: '12px 15px',
                borderRadius: '10px',
                border: '1px solid rgba(0, 200, 255, 0.3)',
                background: 'rgba(10, 15, 30, 0.7)',
                color: '#ffffff',
                outline: 'none',
                transition: 'all 0.3s ease',
                boxShadow: 'inset 0 0 10px rgba(0, 0, 0, 0.3)'
              }}
              onFocus={e => {
                e.target.style.borderColor = '#00ccff';
                e.target.style.boxShadow = '0 0 15px rgba(0, 200, 255, 0.5)';
              }}
              onBlur={e => {
                e.target.style.borderColor = 'rgba(0, 200, 255, 0.3)';
                e.target.style.boxShadow = 'inset 0 0 10px rgba(0, 0, 0, 0.3)';
              }}
              required
            />
          </div>
        ))}
        
        <button
          type="submit"
          style={{
            padding: '14px 20px',
            backgroundColor: '#00cc88',
            color: '#002222',
            border: 'none',
            borderRadius: '10px',
            cursor: 'pointer',
            fontWeight: 'bold',
            fontSize: '1rem',
            transition: 'all 0.3s ease',
            marginTop: '15px',
            boxShadow: '0 0 15px rgba(0, 204, 136, 0.4)',
            textShadow: '0 1px 2px rgba(0, 0, 0, 0.2)'
          }}
          onMouseOver={e => {
            e.target.style.backgroundColor = '#00ffaa';
          }}
          onMouseOut={e => {
            e.target.style.backgroundColor = '#00cc88';
          }}
        >
          Submit {title}
        </button>
      </form>
    </div>
  );
};

