import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const FormSection = ({ title, fields, endpoint, isActive, onClose }) => {
  const [formData, setFormData] = useState({});
  const formRef = useRef(null);

  // Close form when clicking outside - fixed version
  useEffect(() => {
    const handleClick = (event) => {
      // Only close if clicking outside the form
      if (formRef.current && !formRef.current.contains(event.target)) {
        onClose();
      }
    };

    if (isActive) {
      // Add timeout to prevent immediate closure
      const timer = setTimeout(() => {
        document.addEventListener("click", handleClick);
      }, 100);
      
      return () => {
        clearTimeout(timer);
        document.removeEventListener("click", handleClick);
      };
    }
  }, [isActive, onClose]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      
      if (response.ok) {
        alert(`${title} submitted successfully!`);
        setFormData({});
        onClose();
      } else {
        try {
          const errorData = await response.json();
          alert(
            `Failed to submit ${title}: ${errorData.error || errorData.message || "Unknown error"}`
          );
        } catch (parseError) {
          alert(`Failed to submit ${title}: ${response.statusText}`);
        }
      }
    } catch (error) {
      console.error(`Error submitting ${title}:`, error);
      alert(`An error occurred while submitting ${title}.`);
    }
  };

  return (
    <div ref={formRef} style={{
      position: 'fixed',
      top: '50%',
      left: '50%',
      transform: isActive ? 'translate(-50%, -50%) scale(1)' : 'translate(-50%, -50%) scale(0.8)',
      opacity: isActive ? 1 : 0,
      width: '90%',
      maxWidth: '600px',
      backgroundColor: 'rgba(25, 25, 45, 0.97)',
      borderRadius: '20px',
      padding: '25px',
      boxShadow: '0 0 30px rgba(0, 200, 255, 0.5)',
      zIndex: 100,
      transition: 'all 0.4s ease-in-out',
      pointerEvents: isActive ? 'all' : 'none',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(0, 200, 255, 0.3)',
      maxHeight: '90vh',
      overflowY: 'auto'
    }}>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '20px',
        borderBottom: '1px solid rgba(0, 200, 255, 0.3)',
        paddingBottom: '10px'
      }}>
        <h2 style={{ 
          color: '#00ccff', 
          margin: 0,
          textShadow: '0 0 10px rgba(0, 200, 255, 0.7)'
        }}>
          {title}
        </h2>
        <button 
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            color: '#ff5555',
            fontSize: '1.5rem',
            cursor: 'pointer',
            transition: 'transform 0.3s ease',
            textShadow: '0 0 10px rgba(255, 85, 85, 0.7)'
          }}
          onMouseOver={e => e.target.style.transform = 'scale(1.2)'}
          onMouseOut={e => e.target.style.transform = 'scale(1)'}
        >
          ✕
        </button>
      </div>
      
      <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '15px' }}>
        {fields.map((field) => (
          <div key={field.key} style={{ position: 'relative' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              color: '#e0e0ff',
              fontWeight: '500'
            }}>
              {field.label}:
            </label>
            <input
              type="text"
              value={formData[field.key] || ""}
              onChange={(e) => setFormData({ ...formData, [field.key]: e.target.value })}
              style={{
                width: '100%',
                padding: '12px 15px',
                borderRadius: '10px',
                border: '1px solid rgba(0, 200, 255, 0.3)',
                background: 'rgba(10, 15, 30, 0.7)',
                color: '#ffffff',
                outline: 'none',
                transition: 'all 0.3s ease',
                boxShadow: 'inset 0 0 10px rgba(0, 0, 0, 0.3)'
              }}
              onFocus={e => {
                e.target.style.borderColor = '#00ccff';
                e.target.style.boxShadow = '0 0 15px rgba(0, 200, 255, 0.5)';
              }}
              onBlur={e => {
                e.target.style.borderColor = 'rgba(0, 200, 255, 0.3)';
                e.target.style.boxShadow = 'inset 0 0 10px rgba(0, 0, 0, 0.3)';
              }}
              required
            />
          </div>
        ))}
        
        <button
          type="submit"
          style={{
            padding: '14px 20px',
            backgroundColor: '#00cc88',
            color: '#002222',
            border: 'none',
            borderRadius: '10px',
            cursor: 'pointer',
            fontWeight: 'bold',
            fontSize: '1rem',
            transition: 'all 0.3s ease',
            marginTop: '15px',
            boxShadow: '0 0 15px rgba(0, 204, 136, 0.4)',
            textShadow: '0 1px 2px rgba(0, 0, 0, 0.2)'
          }}
          onMouseOver={e => {
            e.target.style.backgroundColor = '#00ffaa';
            e.target.style.transform = 'translateY(-3px)';
          }}
          onMouseOut={e => {
            e.target.style.backgroundColor = '#00cc88';
            e.target.style.transform = 'translateY(0)';
          }}
        >
          Submit {title}
        </button>
      </form>
    </div>
  );
};

const Tables = () => {
  const [activeForm, setActiveForm] = useState(null);
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate("/student-details");
  };

  const formConfigs = [
    {
      key: "recruiter",
      title: "Recruiter Details",
      fields: [
        { key: "companyName", label: "Company Name" },
        { key: "contactPerson", label: "Contact Person" },
        { key: "contactEmail", label: "Contact Email" },
        { key: "contactPhone", label: "Contact Phone" },
        { key: "location", label: "Location" },
      ],
      endpoint: "http://localhost:5000/api/recruiters"
    },
    {
      key: "skill",
      title: "Skill Details",
      fields: [
        { key: "std_id", label: "Student ID" },
        { key: "skill_name", label: "Skill Name" },
        { key: "skill_category", label: "Skill Category" },
