        { key: "std_id", label: "Student ID" },
        { key: "recruit_id", label: "Recruiter ID" },
        { key: "interview_result", label: "Interview Result" },
        { key: "interview_date", label: "Interview Date" },
        { key: "interview_mode", label: "Interview Mode" },
        { key: "feedback", label: "Feedback" },
        { key: "interviewer_name", label: "Interview Name" },
      ],
      endpoint: "http://localhost:5000/api/interviews"
    },
    {
      key: "placement",
      title: "Placement Details",
      fields: [
        { key: "std_id", label: "Student ID" },
        { key: "company_name", label: "Company Name" },
        { key: "job_title", label: "Job Title" },
        { key: "date_of_placement", label: "Date of Placement" },
        { key: "salary_package", label: "Salary" },
        { key: "placed", label: "Placed" },
        { key: "location", label: "Location" },
      ],
      endpoint: "http://localhost:5000/api/placements"
    }
  ];

  return (
    <div style={{
      minHeight: "100vh",
      background: "radial-gradient(circle, #0f0c29, #302b63, #24243e)",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      padding: "20px",
      position: "relative",
      overflow: "hidden"
    }}>
      {/* Animated background elements */}
      <div style={{
        position: "absolute",
        top: "20%",
        left: "10%",
        width: "300px",
        height: "300px",
        borderRadius: "50%",
        background: "radial-gradient(circle, rgba(0, 200, 255, 0.1) 0%, transparent 70%)",
        animation: "float 15s infinite alternate ease-in-out",
        zIndex: 0
      }}></div>
      
      <div style={{
        position: "absolute",
        bottom: "15%",
        right: "10%",
        width: "200px",
        height: "200px",
        borderRadius: "50%",
        background: "radial-gradient(circle, rgba(100, 0, 255, 0.1) 0%, transparent 70%)",
        animation: "float 12s infinite alternate-reverse ease-in-out",
        zIndex: 0
      }}></div>
      
      <div style={{
        position: "absolute",
        top: "40%",
        right: "30%",
        width: "150px",
        height: "150px",
        borderRadius: "50%",
        background: "radial-gradient(circle, rgba(0, 255, 200, 0.1) 0%, transparent 70%)",
        animation: "float 18s infinite alternate ease-in-out",
        zIndex: 0
      }}></div>
      
      {/* Main content */}
      <div style={{
        position: "relative",
        zIndex: 10,
        maxWidth: "1200px",
        margin: "0 auto",
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
      }}>
        <h1 style={{
          color: "#00ccff",
          textAlign: "center",
          marginBottom: "40px",
          fontSize: "2.5rem",
          fontWeight: "bold",
          textShadow: "0 0 15px rgba(0, 200, 255, 0.7)",
          letterSpacing: "1px"
        }}>
          Admin Dashboard
        </h1>
        
        <div style={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "center",
          gap: "25px",
